import './App.css'
import { useState, useEffect } from 'react'
import Navbar from './components/Navbar'
import HomeView from './components/HomeView'
import AboutView from './components/AboutView'
import { Routes, Route } from 'react-router-dom'
import SearchView from './components/SearchView'

function App() {

  const [searchResults, setSearchResults] = useState([])
  const [searchText, setSearchText] = useState([''])

  useEffect(() => {
    console.log(searchText, "is the search text")

    const fetch = require('node-fetch');

    const url = `https://api.themoviedb.org/3/discover/movie?include_adult=false&include_video=false&language=en-US&page=1&sort_by=popularity.desc&with_keywords=${searchText}`;
    const options = {
      method: 'GET',
      headers: {
        accept: 'application/json',
        Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI5MGQ3MDE4Mjc4ZWM4OGZjNzBhMzQxOTAzZjQ5MjExNCIsInN1YiI6IjY0OWIzNjU1ZWEzN2UwMDBmZmUwNjU3ZCIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.enFNIninmzGgeDCg1Uzkxx6FJ0_V1ccuIwokbeADr1s'
      }
    };

    fetch(url, options)
      .then(response => response.json())
      .then(data => {
        console.log(data)
      })


  }, [searchText])  

  return (
    <div className="App">
      <Navbar searchText={searchText} setSearchText={setSearchText} />
      <Routes>
        <Route path="/" element={<HomeView />} />
        <Route path="/about" element={<AboutView />} />
        <Route path ="/search" element={<SearchView keyword={searchText} searchResults={searchResults} />} />
      </Routes>
    </div>
  );
}

export default App;
